import React, { useState, useEffect } from 'react';
import { observer } from 'mobx-react';
import Modal from 'react-bootstrap/Modal';
import { Button, Container, Table, Spinner } from "react-bootstrap";
import CreateCategory from "../components/modals/CreateCategory";
import EditCategory from "../components/modals/EditCategory";
import { fetchCategories as fetchCategoriesAPI, createCategory, updateCategory, deleteCategory } from "../http/deviceAPI"; // Import the deleteCategory function
import './stylepages.css';

const AdmCategory = observer(() => {
    const [categoryVisible, setCategoryVisible] = useState(false);
    const [editVisible, setEditVisible] = useState(false);
    const [deleteVisible, setDeleteVisible] = useState(false); // State for delete modal visibility
    const [categories, setCategories] = useState([]);
    const [loading, setLoading] = useState(true);
    const [error, setError] = useState(null);
    const [selectedCategory, setSelectedCategory] = useState(null);

    const handleUpdateCategories = async () => {
        try {
            const response = await fetchCategoriesAPI();
            setCategories(response);
        } catch (error) {
            console.error('Помилка отримання категорій:', error);
            setError('Не вдалося отримати категорії. Спробуйте ще раз.');
        }
    };

    useEffect(() => {
        fetchCategories();
    }, []);

    const fetchCategories = async () => {
        try {
            const response = await fetchCategoriesAPI();
            setCategories(response);
            setLoading(false);
        } catch (error) {
            console.error('Помилка отримання категорій:', error);
            setError('Не вдалося отримати категорії. Спробуйте ще раз.');
            setLoading(false);
        }
    };

    const addCategory = (categoryData) => {
        createCategory(categoryData)
            .then(() => {
                fetchCategories();
                setCategoryVisible(false);
            })
            .catch(error => {
                setError('Сталася помилка під час додавання категорії');
                console.error('Помилка під час додавання категорії:', error);
            });
    };

    const handleEdit = (category) => {
        if (category) {
            setSelectedCategory(category);
            setEditVisible(true);
        } else {
            console.error("Помилка: Невірний об'єкт категорії");
        }
    };

    const handleDelete = (category) => {
        if (category) {
            setSelectedCategory(category);
            setDeleteVisible(true);
        } else {
            console.error("Помилка: Невірний об'єкт категорії");
        }
    };

    const confirmDelete = async () => {
        try {
            await deleteCategory(selectedCategory.id);
            fetchCategories();
            setDeleteVisible(false);
        } catch (error) {
            setError('Помилка при видаленні категорії');
            console.error('Помилка при видаленні категорії:', error);
        }
    };

    const handleUpdateCategory = (updatedCategory) => {
        updateCategory(updatedCategory)
            .then(() => {
                fetchCategories();
                setEditVisible(false);
            })
            .catch(error => {
                setError('Сталася помилка під час оновлення категорії');
                console.error('Помилка під час оновлення категорії:', error);
            });
    };

    return (
        <Container className="d-flex flex-column">
            {loading ? (
                <Spinner animation="border" role="status">
                    <span className="sr-only">Завантаження...</span>
                </Spinner>
            ) : error ? (
                <p>{error}</p>
            ) : (
                <>
                    <Table striped bordered hover className="mt-4">
                        <thead>
                            <tr>
                                <th>Категорія</th>
                                <th>ID</th>
                                <th>Дії</th>
                            </tr>
                        </thead>
                        <tbody>
                            {categories.map(category => (
                                <tr key={category.id}>
                                    <td>{category.name}</td>
                                    <td>{category.id}</td>
                                    <td>
                                        <Button variant="primary" onClick={() => handleEdit(category)}>Редагувати</Button>{' '}
                                        <Button variant="danger" onClick={() => handleDelete(category)}>Видалити</Button>
                                    </td>
                                </tr>
                            ))}
                        </tbody>
                    </Table>

                    <Button className="add-button" onClick={() => setCategoryVisible(true)} >
                        Додати категорію
                    </Button>
                    <CreateCategory
                        show={categoryVisible}
                        onHide={() => setCategoryVisible(false)}
                        onUpdateCategories={handleUpdateCategories}
                    />
                    <EditCategory
                        show={editVisible}
                        onHide={() => setEditVisible(false)}
                        onUpdateCategory={handleUpdateCategory}
                        category={selectedCategory}
                    />
                    <Modal show={deleteVisible} onHide={() => setDeleteVisible(false)} centered>
                        <Modal.Header closeButton>
                            <Modal.Title>Підтвердіть видалення</Modal.Title>
                        </Modal.Header>
                        <Modal.Body>
                            <p>Видалити категорію?</p>
                        </Modal.Body>
                        <Modal.Footer>
                            <Button variant="secondary" onClick={() => setDeleteVisible(false)}>
                                Скасувати
                            </Button>
                            <Button variant="danger" onClick={confirmDelete}>
                                Видалити
                            </Button>
                        </Modal.Footer>
                    </Modal>
                </>
            )}
        </Container>
    );
});

export default AdmCategory;
