import React, { useState } from 'react';
import { Button, Form } from 'react-bootstrap';
import { createManager } from '../../../http/deviceAPI'; 

const CreateManager = ({ show, onHide, onUpdateMasters }) => {
    const [name, setName] = useState('');
    const [password, setPassword] = useState('');
    const [phone, setPhone] = useState('');
    const [age, setAge] = useState('');
    const [email, setEmail] = useState('');
    const [surname, setSurname] = useState('');
    const [error, setError] = useState(null);

    const addManager = () => {
        if (!name || !password || !phone || !age || !email || !surname) {
            setError('Please fill in all fields');
            return;
        }

        createManager({ name, password, phone, age, email, surname })
        .then((data) => {
            onUpdateManagers(); // Call the callback function to update the master list
            setName('');
            setPassword('');
            setPhone('');
            setAge('');
            setEmail('');
            setSurname('');
            setError(null);
            onHide();
        })
        .catch(error => {
            setError('An error occurred while adding the master');
            console.error('Error adding master:', error);
        });
};


    return (
        <Form onSubmit={handleSubmit}>
            <Form.Group controlId="formManagerName">
                <Form.Label>Name</Form.Label>
                <Form.Control
                    type="text"
                    placeholder="Enter name"
                    name="name"
                    value={managerData.name}
                    onChange={handleChange}
                />
            </Form.Group>
            <Form.Group controlId="formManagerPassword">
                <Form.Label>Password</Form.Label>
                <Form.Control
                    type="password"
                    placeholder="Enter password"
                    name="password"
                    value={managerData.password}
                    onChange={handleChange}
                />
            </Form.Group>
            <Form.Group controlId="formManagerPhone">
                <Form.Label>Phone</Form.Label>
                <Form.Control
                    type="text"
                    placeholder="Enter phone"
                    name="phone"
                    value={managerData.phone}
                    onChange={handleChange}
                />
            </Form.Group>
            <Form.Group controlId="formManagerAge">
                <Form.Label>Age</Form.Label>
                <Form.Control
                    type="text"
                    placeholder="Enter age"
                    name="age"
                    value={managerData.age}
                    onChange={handleChange}
                />
            </Form.Group>
            <Form.Group controlId="formManagerEmail">
                <Form.Label>Email</Form.Label>
                <Form.Control
                    type="email"
                    placeholder="Enter email"
                    name="email"
                    value={managerData.email}
                    onChange={handleChange}
                />
            </Form.Group>
            <Form.Group controlId="formManagerSurname">
                <Form.Label>Surname</Form.Label>
                <Form.Control
                    type="text"
                    placeholder="Enter surname"
                    name="surname"
                    value={managerData.surname}
                    onChange={handleChange}
                />
            </Form.Group>
            <Button variant="primary" type="submit">
                Create Manager
            </Button>
            {error && <p style={{ color: 'red' }}>{error}</p>}
        </Form>
    );
};

export default CreateManager;
