import React, { useState, useEffect } from 'react';
import Modal from 'react-bootstrap/Modal';
import { Button, Form } from 'react-bootstrap';
import { createProcedure, fetchCategories } from '../../../http/deviceAPI';

const CreateProcedure = ({ show, onHide, onUpdateProcedures }) => {
  const [name, setName] = useState('');
  const [description, setDescription] = useState('');
  const [price, setPrice] = useState('');
  const [categories, setCategories] = useState([]);
  const [selectedCategory, setSelectedCategory] = useState('');
  const [image, setImage] = useState(null);
  const [error, setError] = useState(null);

  const addProcedure = async () => {
    try {
      if (!name || !description || !price || !selectedCategory || !image) {
        throw new Error('Please fill out all fields');
      }

      await createProcedure({ name, description, price, category_id: selectedCategory, image });
      onUpdateProcedures();
      setName('');
      setDescription('');
      setPrice('');
      setSelectedCategory('');
      setImage(null);
      setError(null);
      onHide();
    } catch (error) {
      setError('Помилка додавання процедури: ' + error.message);
      console.error('Помилка додавання процедури:', error);
    }
  };

  useEffect(() => {
    const fetchData = async () => {
      try {
        const categoriesData = await fetchCategories();
        setCategories(categoriesData);
      } catch (error) {
        setError('Помилка отримання категорій: ' + error.message);
        console.error('Помилка отримання категорій:', error);
      }
    };
    fetchData();
  }, []);

  const handleImageChange = (e) => {
    setImage(e.target.files[0]);
  };

  return (
    <Modal show={show} onHide={onHide} centered>
      <Modal.Header closeButton>
        <Modal.Title>Додати процедуру</Modal.Title>
      </Modal.Header>
      <Modal.Body>
        {error && <p className="text-danger">{error}</p>}
        <Form>
          <Form.Group controlId="formProcedureName">
            <Form.Label>Назва</Form.Label>
            <Form.Control
              type="text"
              placeholder="Введіть назву процедури"
              value={name}
              onChange={(e) => setName(e.target.value)}
            />
          </Form.Group>
          {/* Other form groups */}
          <Form.Group controlId="formProcedureImage">
            <Form.Label>Зображення</Form.Label>
            <Form.Control type="file" onChange={handleImageChange} />
          </Form.Group>
        </Form>
      </Modal.Body>
      <Modal.Footer>
        <Button className='btn-cancel' onClick={onHide}>
          Скасувати
        </Button>
        <Button className='btn-save' onClick={addProcedure}>
          Зберегти
        </Button>
      </Modal.Footer>
    </Modal>
  );
};

export default CreateProcedure;
