// Ваш файл компонента AdmCategory
import React, { useState, useEffect } from 'react';
import { observer } from 'mobx-react';
import Modal from 'react-bootstrap/Modal';
import { Button, Container, Table, Spinner } from "react-bootstrap";
import CreateCategory from "../components/modals/categories/CreateCategory";
import EditCategory from "../components/modals/EditCategory";
import { fetchCategories as fetchCategoriesAPI, deleteCategory } from "../http/deviceAPI"; // Убрали импорт updateCategory, так как он не нужен
import './stylepages.css';

const AdmCategory = observer(() => {
    const [categoryVisible, setCategoryVisible] = useState(false);
    const [editVisible, setEditVisible] = useState(false);
    const [deleteVisible, setDeleteVisible] = useState(false);
    const [categories, setCategories] = useState([]);
    const [loading, setLoading] = useState(true);
    const [error, setError] = useState(null);
    const [selectedCategory, setSelectedCategory] = useState(null);

    const fetchCategories = async () => {
        try {
            const response = await fetchCategoriesAPI();
            setCategories(response);
            setLoading(false);
        } catch (error) {
            console.error('Помилка отримання категорій:', error);
            setError('Не вдалося отримати категорії. Спробуйте ще раз.');
            setLoading(false);
        }
    };

    const handleEdit = (category) => {
        setSelectedCategory(category);
        setEditVisible(true);
    };

    const handleDelete = (category) => {
        setSelectedCategory(category);
        setDeleteVisible(true);
    };

    const confirmDelete = async () => {
        try {
            await deleteCategory(selectedCategory.category_id);
            fetchCategories();
            setDeleteVisible(false);
        } catch (error) {
            setError(error.message);
            console.error('Помилка при видаленні категорії:', error);
        }
    };

    useEffect(() => {
        fetchCategories();
    }, []);

    // Функция для обновления списка категорий после успешного обновления категории
    const handleUpdateCategory = async () => {
        try {
            const updatedCategories = await fetchCategoriesAPI();
            setCategories(updatedCategories);
        } catch (error) {
            console.error('Помилка при оновленні списку категорій:', error);
        }
    };

    return (
        <Container className="d-flex flex-column">
            {loading ? (
                <Spinner animation="border" role="status">
                    <span className="sr-only">Завантаження...</span>
                </Spinner>
            ) : error ? (
                <p>{error}</p>
            ) : (
                <>
                    <Table striped bordered hover className="mt-4">
                        <thead>
                            <tr>
                                <th>Категорія</th>
                                <th>ID</th>
                                <th>Дії</th>
                            </tr>
                        </thead>
                        <tbody>
                            {categories.map(category => (
                                <tr key={category.id}>
                                    <td>{category.name}</td>
                                    <td>{category.category_id}</td>
                                    <td>
                                        <Button variant="primary" onClick={() => handleEdit(category)}>Редагувати</Button>{' '}
                                        <Button variant="danger" onClick={() => handleDelete(category)}>Видалити</Button>
                                    </td>
                                </tr>
                            ))}
                        </tbody>
                    </Table>

                    <Button className="add-button" onClick={() => setCategoryVisible(true)} >
                        Додати категорію
                    </Button>
                    <CreateCategory
                        show={categoryVisible}
                        onHide={() => setCategoryVisible(false)}
                        onUpdateCategories={fetchCategories}
                    />
                    <EditCategory
                        show={editVisible}
                        onHide={() => setEditVisible(false)}
                        onUpdateCategory={handleUpdateCategory} // Передаем новую функцию обновления категорий
                        category={selectedCategory}
                    />
                    <Modal show={deleteVisible} onHide={() => setDeleteVisible(false)} centered>
                        <Modal.Header closeButton>
                            <Modal.Title>Підтвердіть видалення</Modal.Title>
                        </Modal.Header>
                        <Modal.Body>
                            <p>Видалити категорію?</p>
                        </Modal.Body>
                        <Modal.Footer>
                            <Button variant="secondary" onClick={() => setDeleteVisible(false)}>
                                Скасувати
                            </Button>
                            <Button variant="danger" onClick={confirmDelete}>
                                Видалити
                            </Button>
                        </Modal.Footer>
                    </Modal>
                </>
            )}
        </Container>
    );
});

export default AdmCategory;
