import React, { useState } from 'react';
import Modal from 'react-bootstrap/Modal';
import { Button, Form } from 'react-bootstrap';

const EditCategory = ({ show, onHide, onUpdateCategories, category }) => {
    const initialName = category && category.name ? category.name : '';
    const initialDescription = category && category.description ? category.description : '';

    const [name, setName] = useState(initialName);
    const [description, setDescription] = useState(initialDescription);
    const [error, setError] = useState(null);

    const handleUpdate = () => {
        if (!name || !description) {
            setError('Будь ласка, заповніть усі поля');
            return;
        }

        const updatedCategory = {
            ...category,
            name: name,
            description: description
        };

        onUpdateCategories(updatedCategory);
        onHide();
    };

    return (
        <Modal show={show} onHide={onHide} centered>
            <Modal.Header closeButton>
                <Modal.Title>Редагувати категорію</Modal.Title>
            </Modal.Header>
            <Modal.Body>
                <Form>
                    <Form.Group controlId="formCategoryName">
                        <Form.Label>Назва</Form.Label>
                        <Form.Control
                            type="text"
                            placeholder="Введіть назву категорії"
                            value={name || ''}
                            onChange={(e) => setName(e.target.value)}
                        />
                    </Form.Group>
                    <Form.Group controlId="formCategoryDescription">
                        <Form.Label>Опис</Form.Label>
                        <Form.Control
                            type="text"
                            placeholder="Введіть опис категорії"
                            value={description || ''}
                            onChange={(e) => setDescription(e.target.value)}
                        />
                    </Form.Group>
                </Form>
                {error && <p style={{ color: 'red' }}>{error}</p>}
            </Modal.Body>
            <Modal.Footer>
                <Button variant="secondary" onClick={onHide}>
                    Скасувати
                </Button>
                <Button variant="primary" onClick={handleUpdate}>
                    Зберегти
                </Button>
            </Modal.Footer>
        </Modal>
    );
};

export default EditCategory;
