import React from "react";
import { Container } from "react-bootstrap";
import { observer } from "mobx-react-lite";
import HomePage from "../components/HomePage";
import AboutUs from "../components/AboutUs";
import CategoryList from "../components/CategoryList"; // Import CategoryList component
import { useContext } from 'react';
import { MobXProviderContext } from 'mobx-react';

import './stylepages.css';



export const useStores = () => {
  const stores = useContext(MobXProviderContext);
  if (!stores) {
    throw new Error('useStores must be used within a MobX Provider');
  }
  return stores;
};

const Home = observer(() => {
    const { categoryStore } = useStores(); // Access CategoryStore from MobX store



    return (
        <Container>
           <HomePage/>
           <AboutUs/>
           <CategoryList categories={categoryStore.categories} /> {/* Pass categories from MobX store to CategoryList */}
        </Container>
    );
});

export default Home;
