import React from 'react';
import { NavLink } from 'react-router-dom';
import { SALON_ROUTE } from '../utils/consts';

const SalonItem = ({ salon }) => {
  return (
    <div className="salon-item">
      <NavLink to={`${SALON_ROUTE}/${salon.salon_id}`} className='navlink'>
        <img src={process.env.REACT_APP_API_URL + salon.img} alt={salon.name} style={{ width: '200px', height: '100px' }} />
        <h7>{salon.name}</h7>
        <h1>{salon.address}</h1>
        <p>Відвідати нас :</p>
        <p>{salon.address}</p>
        <p>Написати нам :</p>
        <p>KRASASUPPORT@GMAIL.COM</p>
        <p>Подзвонити нам :</p>
        <p>+380 (67) 123 - 45 - 67</p>
      </NavLink>
    </div>
  );
};

export default SalonItem;
