/* eslint-disable react-hooks/exhaustive-deps */
import React, { useContext, useEffect, useCallback } from 'react';
import { Container } from "react-bootstrap";
import { observer } from "mobx-react-lite";
import { Context } from "../index";
import HomePage from "../components/HomePage";
import AboutUs from "../components/AboutUs";
import { fetchCategories, fetchProcedures } from "../http/deviceAPI";
import './stylepages.css';

const Salon = observer(() => {
    const { procedure } = useContext(Context);

    const setCategories = useCallback((data) => {
        procedure.setCategories(data);
    }, [procedure.setCategories]);

    const setProcedures = useCallback((data) => {
        procedure.setProcedures(data);
    }, [procedure.setProcedures]);

    const setTotalCount = useCallback((count) => {
        procedure.setTotalCount(count);
    }, [procedure.setTotalCount]);

    useEffect(() => {
        fetchCategories().then(data => setCategories(data));
        fetchProcedures(null, null, 1, 2).then(data => {
            setProcedures(data.rows);
            setTotalCount(data.count);
        });
    }, [setCategories, setProcedures, setTotalCount]);

    useEffect(() => {
        fetchProcedures(procedure.selectedCategory.id, null, procedure.page, 2).then(data => {
            setProcedures(data.rows);
            setTotalCount(data.count);
        });
    }, [procedure.page, procedure.selectedCategory, setProcedures, setTotalCount]);

    return (
        <Container>
           <HomePage/>
           <AboutUs/>
        </Container>
    );
});

export default Salon;
