import React from 'react';
import { Card, Col} from 'react-bootstrap';
import Image from 'react-bootstrap/Image';
import { useNavigate } from 'react-router-dom';
import { CATEGORY_ROUTE } from '../utils/consts';


const CategoryItem = ({ category }) => {
  const navigate = useNavigate();
  return (
    <Col md={4} className={'mt-5'} onClick={() => navigate(CATEGORY_ROUTE + '/' + category.id)}>
      <Card style={{ width: 250, cursor: 'pointer' }} border={'light'}>
        <Image width={250} height={300} src={process.env.REACT_APP_API_URL + category.img} />
        <div className="text-black-50 mt-1 d-flex justify-content-between align-items-center">
            <div>{category.name} </div>
        </div>
        <div>{category.name}</div>
      </Card>
    </Col>
  );
};

export default CategoryItem;
