import React, { useState, useEffect } from 'react';
import Modal from 'react-bootstrap/Modal';
import { Button, Form } from 'react-bootstrap';
import { updateCategory } from '../../../http/deviceAPI';

const EditCategory = ({ show, onHide, onUpdateCategory, category }) => {
    const [name, setName] = useState(category ? category.name : '');
    const [description, setDescription] = useState(category ? category.description : '');
    const [error, setError] = useState(null);
    const [isModified, setIsModified] = useState(false);

    useEffect(() => {
        setIsModified(false); // Сбрасываем флаг при открытии модального окна
    }, [show]);

    const handleUpdate = async () => {
        if (!name || !description) {
            setError('Будь ласка, заповніть усі поля');
            return;
        }

        const updatedCategory = {
            ...category,
            name: name,
            description: description
        };

        try {
            await updateCategory(category.category_id, updatedCategory);
            onUpdateCategory();
            onHide();
        } catch (error) {
            setError(error.message);
        }
    };

    const handleNameChange = (e) => {
        setName(e.target.value);
        setIsModified(true); // Помечаем, что поле было изменено
    };

    const handleDescriptionChange = (e) => {
        setDescription(e.target.value);
        setIsModified(true); // Помечаем, что поле было изменено
    };

    return (
        <Modal show={show} onHide={onHide} centered>
            <Modal.Header closeButton>
                <Modal.Title>Редагувати категорію</Modal.Title>
            </Modal.Header>
            <Modal.Body>
                <Form>
                    <Form.Group controlId="formCategoryName">
                        <Form.Label>Назва</Form.Label>
                        <Form.Control
                            type="text"
                            placeholder="Введіть назву категорії"
                            value={name}
                            onChange={handleNameChange}
                        />
                    </Form.Group>
                    <Form.Group controlId="formCategoryDescription">
                        <Form.Label>Опис</Form.Label>
                        <Form.Control
                            type="text"
                            placeholder="Введіть опис категорії"
                            value={description}
                            onChange={handleDescriptionChange}
                        />
                    </Form.Group>
                </Form>
                {error && <p style={{ color: 'red' }}>{error}</p>}
            </Modal.Body>
            <Modal.Footer>
                <Button variant="secondary" onClick={onHide}>
                    Скасувати
                </Button>
                <Button variant="primary" onClick={handleUpdate} disabled={!isModified}>
                    Зберегти
                </Button>
            </Modal.Footer>
        </Modal>
    );
};

export default EditCategory;
