import React, { useContext } from 'react';
import { Context } from '../index';
import Navbar from 'react-bootstrap/Navbar';
import Nav from 'react-bootstrap/Nav';
import { NavLink, useNavigate } from 'react-router-dom';
import { ADMIN_ROUTE, LOGIN_ROUTE, SHOP_ROUTE, BASKET_ROUTE } from '../utils/consts';
import { Button } from 'react-bootstrap';
import { observer } from 'mobx-react-lite';
import Container from 'react-bootstrap/Container';
import Logo from './img/Logo.png';

const NavBar = observer(() => {
  const { user } = useContext(Context);
  const navigate = useNavigate();

  const logOut = () => {
    user.setUser({});
    user.setIsAuth(false);
  };

  return (
    <Navbar bg="#FFFFFF" variant="dark" className="py-4">
      <Container>
        <NavLink to={SHOP_ROUTE}>
          <Button variant="FFFFFF" className="text-dark rounded d-flex align-items-center">
          <img src={Logo} alt="Logo" className="mr-2" style={{ height: '20px' }} />
          <span>Краса</span>
          </Button>
        </NavLink>
        {user.isAuth ? (
          <Nav className="ml-auto">
            <Button variant="dark" className="text-white rounded mr-2" onClick={() => navigate(BASKET_ROUTE)}>
              Кошик
            </Button>
            <Button variant="dark" className="text-white rounded" onClick={() => navigate(ADMIN_ROUTE)}>
              Додати пост
            </Button>
            <Button variant="dark" className="text-white rounded ml-2" onClick={() => logOut()}>
              Вийти
            </Button>
          </Nav>
        ) : (
          <Nav className="ml-auto">
            <Button variant="dark" className="text-white rounded" onClick={() => navigate(LOGIN_ROUTE)}>
              Авторизація
            </Button>
          </Nav>
        )}
      </Container>
    </Navbar>
  );
});

export default NavBar;
