import React, { useState, useEffect } from 'react';
import Modal from 'react-bootstrap/Modal';
import { Button, Form } from 'react-bootstrap';
import { getCategoryById, updateCategory } from '../../http/deviceAPI'; // Подключаем функции для работы с категориями

const EditCategory = ({ show, onHide, categoryId, onUpdateCategories }) => {
  const [name, setName] = useState('');
  const [description, setDescription] = useState('');
  const [error, setError] = useState(null);

  useEffect(() => {
    const fetchCategory = async () => {
      try {
        const category = await getCategoryById(categoryId);
        setName(category.name);
        setDescription(category.description);
      } catch (error) {
        console.error('Error fetching category:', error);
        setError('Failed to fetch category. Please try again.');
      }
    };

    if (categoryId) {
      fetchCategory();
    }
  }, [categoryId]);

  const handleUpdateCategory = async () => {
    try {
      await updateCategory(categoryId, { name, description });
      onUpdateCategories(); // Обновляем список категорий после успешного обновления
      onHide(); // Закрываем модальное окно
    } catch (error) {
      console.error('Error updating category:', error);
      setError('Failed to update category. Please try again.');
    }
  };

  return (
    <Modal show={show} onHide={onHide} centered>
      <Modal.Header closeButton>
        <Modal.Title>Редактировать категорию</Modal.Title>
      </Modal.Header>
      <Modal.Body>
        <Form>
          <Form.Group controlId="formCategoryName">
            <Form.Label>Название</Form.Label>
            <Form.Control
              type="text"
              placeholder="Введите название категории"
              value={name}
              onChange={(e) => setName(e.target.value)}
            />
          </Form.Group>
          <Form.Group controlId="formCategoryDescription">
            <Form.Label>Описание</Form.Label>
            <Form.Control
              type="text"
              placeholder="Введите описание категории"
              value={description}
              onChange={(e) => setDescription(e.target.value)}
            />
          </Form.Group>
        </Form>
        {error && <p style={{ color: 'red' }}>{error}</p>}
      </Modal.Body>
      <Modal.Footer>
        <Button variant="secondary" onClick={onHide}>
          Отмена
        </Button>
        <Button variant="primary" onClick={handleUpdateCategory}>
          Сохранить
        </Button>
      </Modal.Footer>
    </Modal>
  );
};

export default EditCategory;
