import React, { useContext, useEffect } from 'react';
import { observer } from "mobx-react-lite";
import { Context } from "../index";
import { Row } from "react-bootstrap";
import CategoryItem from "./CategoryItem";

const CategoryList = observer(() => {
    const { procedureSalon } = useContext(Context);

    useEffect(() => {
        // Загрузка категорий при монтировании компонента
        fetchCategories().then(categories => procedureSalon.setCategories(categories));
    }, [procedureSalon]); // Добавляем procedureSalon в зависимости useEffect

    return (
        <Row className="d-flex">
            {procedureSalon.categories.map(category => // Исправляем categories
                <CategoryItem key={category.id} category={category}/>
            )}
        </Row>
    );
});

export default CategoryList;
