import React, { useState, useEffect } from 'react';
import { observer } from 'mobx-react';
import { Modal, Button, Container, Table, Spinner } from 'react-bootstrap';
import { PencilSquare, Trash } from 'react-bootstrap-icons'; 
import CreateSalon from '../components/modals/salons/CreateSalon';
import EditSalon from '../components/modals/salons/EditSalon';
import { fetchSalons as fetchSalonsAPI, deleteSalon } from "../http/deviceAPI";
import './stylepages.css';

const AdmSalon = observer(() => {
    const [salons, setSalons] = useState([]);
    const [loading, setLoading] = useState(true);
    const [error, setError] = useState(null);
    const [createModalVisible, setCreateModalVisible] = useState(false);
    const [editModalVisible, setEditModalVisible] = useState(false);
    const [deleteModalVisible, setDeleteModalVisible] = useState(false);
    const [selectedSalon, setSelectedSalon] = useState(null);

    // Function to fetch salons from the server
    const fetchSalons = async () => {
        try {
            const salonsData = await fetchSalonsAPI();
            setSalons(salonsData);
            setLoading(false);
        } catch (error) {
            console.error('Error fetching salons:', error);
            setError('Failed to fetch salons. Please try again.');
            setLoading(false);
        }
    };

    // Function to handle deletion of a salon
    const handleDeleteSalon = async (salon) => {
        setSelectedSalon(salon);
        setDeleteVisible(true);
    };

    useEffect(() => {
        fetchSalons();
    }, []); // Fetch salons on component mount

    // Function to open edit salon modal
    const handleEditSalon = (salon) => {
        setSelectedSalon(salon);
        setEditModalVisible(true);
    };

    const confirmDelete = async () => {
        try {
            await deleteSalon(selectedSalon.salon_id);
            fetchSalons();
            setDeleteVisible(false);
        } catch (error) {
            console.error('Помилка видалення салону:', error);
            setError('Помилка видалення салону. Спробуйте ще раз.');
        }
    };

    const handleUpdateSalon = async () => {
        try {
            const updatedSalons = await fetchSalonsAPI();
            setSalons(updatedSalons);
        } catch (error) {
            console.error('Помилка при оновленні списку салонів:', error);
        }
    };

    return (
        <Container className="d-flex flex-column">
            {loading ? (
                <Spinner animation="border" role="status">
                    <span className="sr-only">Loading...</span>
                </Spinner>
            ) : error ? (
                <p>{error}</p>
            ) : (
                <>
                    <Table striped bordered hover className="mt-4">
                        <thead>
                            <tr>
                                <th>Name</th>
                                <th>Address</th>
                                <th>Description</th>
                                <th>Actions</th>
                            </tr>
                        </thead>
                        <tbody>
                            {salons.map((salon) => (
                                <tr key={salon.salon_id}>
                                    <td>{salon.name}</td>
                                    <td>{salon.address}</td>
                                    <td>{salon.description}</td>
                                    <td>
                                        <Button variant="primary" onClick={() => handleEditSalon(salon)}>
                                            <PencilSquare />
                                        </Button>
                                        <Button variant="danger" onClick={() => handleDeleteSalonModal(salon)}>
                                            <Trash />
                                        </Button>
                                    </td>
                                </tr>
                            ))}
                        </tbody>
                    </Table>
                    <Button onClick={() => setCreateModalVisible(true)}>Add Salon</Button>
                    <CreateSalon
                        show={createModalVisible}
                        onHide={() => setCreateModalVisible(false)}
                        onSalonCreated={fetchSalonsData} // Callback to refresh salon list after creation
                    />
                    <EditSalon
                        show={editModalVisible}
                        onHide={() => setEditModalVisible(false)}
                        salon={selectedSalon}
                        onSalonUpdated={handleUpdateSalon} // Callback to refresh salon list after update
                    />
                    <Modal show={deleteModalVisible} onHide={() => setDeleteModalVisible(false)}>
                        <Modal.Header closeButton>
                            <Modal.Title>Delete Salon</Modal.Title>
                        </Modal.Header>
                        <Modal.Body>
                            Are you sure you want to delete this salon?
                        </Modal.Body>
                        <Modal.Footer>
                            <Button variant="secondary" onClick={() => setDeleteModalVisible(false)}>
                                Cancel
                            </Button>
                            <Button variant="danger" onClick={() => handleDeleteSalon(selectedSalon.salon_id)}>
                                Delete
                            </Button>
                        </Modal.Footer>
                    </Modal>
                </>
            )}
        </Container>
    );
});

export default AdmSalon;
