import React from 'react';
import { Card, Col } from 'react-bootstrap';
import Image from 'react-bootstrap/Image';
import { useNavigate } from 'react-router-dom';
import { DEVICE_ROUTE } from '../utils/consts';

const DeviceItem = ({ device }) => {
    const navigate = useNavigate();
    const { basket } = useContext(Context);

    const handleRemoveItem = () => {
      basket.removeItem(device);
    };

    const handleIncrement = () => {
      basket.incrementQuantity(device);
    };

    const handleDecrement = () => {
      basket.decrementQuantity(device);
    };

    return (
        <Col md={4} className={'mt-5'} onClick={() => navigate(DEVICE_ROUTE + '/' + device.id)}>
            <Card style={{ width: 250, cursor: 'pointer' }} border={'light'}>
                <Image width={250} height={300} src={process.env.REACT_APP_API_URL + device.img} />
                <div className="text-black-50 mt-1 d-flex justify-content-between align-items-center">
                    <div><div>{device.price} грн</div></div>
                </div>
                <div>{device.name}</div>
                <ButtonGroup>
                    <Button variant="secondary" onClick={handleDecrement}>
                        -
                    </Button>
                    <Button variant="light" disabled>
                        {device.quantity}
                    </Button>
                    <Button variant="secondary" onClick={handleIncrement}>
                        +
                    </Button>
                    <Button variant="danger" onClick={handleRemoveItem}>
                        <span aria-hidden="true">×</span>
                    </Button>
                </ButtonGroup>
            </Card>
        </Col>
    );
};

export default DeviceItem;
