//Profile.js
import { useContext } from 'react';
import { Container, Row, Col, Image, Button } from 'react-bootstrap';
import { observer } from 'mobx-react-lite';
import './stylepages.css';
import ButtonGroup from './ButtonGroup';
import ProfileImg from './img/homeimg.png';

// Импортируем контекст или хук для получения данных пользователя
import { Context } from '../index'; // Замените на путь к вашему контексту или хуку

const Profile = observer(() => {
  // Получаем данные о пользователе из контекста или хука
  const { user } = useContext(Context); // Предполагается, что в контексте есть объект user с данными о пользователе

  return (
    <Container className="profile-container">
      <ButtonGroup />
      <Col className="u-prof-container">
        <Row className="profile-row">
          <Col xs={12} md={4}>
            <Image src={ProfileImg} roundedCircle fluid />
          </Col>
          <Col xs={12} md={8}>
            <h2>{user.user.name} {user.user.surname}</h2> {/* Отображаем имя и фамилию */}
            <Col className="profile-p">
              <div>
                <p>{user.user.phone}</p> {/* Отображаем номер телефона */}
                <p>{user.user.email}</p> {/* Отображаем email */}
              </div>
            </Col>
          </Col>
        </Row>
        <Row>
          <Col>
            <p className='ml-3'>Опис.</p>
            <Button className="prof-button">Редагувати</Button>
          </Col>
        </Row>
      </Col>
    </Container>
  );
});

export default Profile;
