import { useState } from "react";
import { Container, Button, Form } from "react-bootstrap"; // Импорт компонента Form из react-bootstrap
import AdmCategory from "./AdmCategory";
import AdmProcedure from "./AdmProcedure";
import './stylepages.css';

const Admin = () => {
    const [showCategory, setShowCategory] = useState(false);
    const [showProcedure, setShowProcedure] = useState(false);
    const [searchQuery, setSearchQuery] = useState(""); // Состояние для хранения поискового запроса

    const handleCategoryClick = () => {
        setShowCategory(true);
        setShowProcedure(false);
    };

    const handleProcedureClick = () => {
        setShowCategory(false);
        setShowProcedure(true);
    };

    const handleSearchChange = (event) => {
        setSearchQuery(event.target.value);
    };

    return (
        <Container className="adm-container">
            <div className="button-container">
                <Button className="custom-button" onClick={handleCategoryClick} >Категорії</Button>
                <Button  className="custom-button" onClick={handleProcedureClick}>Процедури</Button>
            </div>

            <div className="adm-table">
            <Form.Control // Добавление строки поиска
                type="text"
                placeholder="Поиск..."
                value={searchQuery}
                onChange={handleSearchChange}
            />
                {/* Передача поискового запроса в компоненты для фильтрации данных */}
                {showCategory && <AdmCategory searchQuery={searchQuery} />}
                {showProcedure && <AdmProcedure searchQuery={searchQuery} />}
            </div>
        </Container>
    );
};

export default Admin;
