import { useState } from "react";
import { Container, Button, Form } from "react-bootstrap";
import AdmCategory from "./AdmCategory";
import AdmProcedure from "./AdmProcedure";
import AdmSalon from "./AdmSalon";
import AdmMaster from "./AdmMaster";
import AdmClient from "./AdmClient";
import AdmManager from "./AdmManager"; // Import the AdmManager component

import './stylepages.css';

const Admin = () => {
    const [showCategory, setShowCategory] = useState(false);
    const [showProcedure, setShowProcedure] = useState(false);
    const [showSalon, setShowSalon] = useState(false);
    const [showMaster, setShowMaster] = useState(false);
    const [showClient, setShowClient] = useState(false);
    const [showManager, setShowManager] = useState(false); // State variable for showing manager table
    const [searchQuery, setSearchQuery] = useState("");

    const handleCategoryClick = () => {
        setShowCategory(true);
        setShowProcedure(false);
        setShowSalon(false);
        setShowMaster(false);
        setShowClient(false);
        setShowManager(false); // Close manager table when switching to other tabs
    };

    const handleProcedureClick = () => {
        setShowCategory(false);
        setShowProcedure(true);
        setShowSalon(false);
        setShowMaster(false);
        setShowClient(false);
        setShowManager(false); // Close manager table when switching to other tabs
    };

    const handleSalonClick = () => {
        setShowCategory(false);
        setShowProcedure(false);
        setShowSalon(true);
        setShowMaster(false);
        setShowClient(false);
        setShowManager(false); // Close manager table when switching to other tabs
    };

    const handleMasterClick = () => {
        setShowCategory(false);
        setShowProcedure(false);
        setShowSalon(false);
        setShowMaster(true);
        setShowClient(false);
        setShowManager(false); // Close manager table when switching to other tabs
    };

    const handleClientClick = () => {
        setShowCategory(false);
        setShowProcedure(false);
        setShowSalon(false);
        setShowMaster(false);
        setShowClient(true);
        setShowManager(false); // Close manager table when switching to other tabs
    };

    const handleManagerClick = () => {
        setShowManager(true); // Show manager table
    };

    const handleSearchChange = (event) => {
        setSearchQuery(event.target.value);
    };

    return (
        <Container className="adm-container">
            <div className="tab-button-container">
                <Button className="tab-custom-button" onClick={handleCategoryClick}>Категорії</Button>
                <Button className="tab-custom-button" onClick={handleProcedureClick}>Процедури</Button>
                <Button className="tab-custom-button" onClick={handleSalonClick}>Салони</Button>
                <Button className="tab-custom-button" onClick={handleMasterClick}>Майстри</Button>
                <Button className="tab-custom-button" onClick={handleClientClick}>Клієнти</Button>
                <Button className="tab-custom-button" onClick={handleManagerClick}>Менеджери</Button> {/* Button to display manager table */}
            </div>

            <div className="adm-table">
                <Form.Control
                    type="text"
                    placeholder="Пошук..."
                    value={searchQuery}
                    onChange={handleSearchChange}
                />
                {/* Conditionally render manager table */}
                {showCategory && <AdmCategory searchQuery={searchQuery} />}
                {showProcedure && <AdmProcedure searchQuery={searchQuery} />}
                {showSalon && <AdmSalon searchQuery={searchQuery} />}
                {showMaster && <AdmMaster searchQuery={searchQuery} />}
                {showClient && <AdmClient searchQuery={searchQuery} />}
                {showManager && <AdmManager searchQuery={searchQuery} />}
            </div>
        </Container>
    );
};

export default Admin;
