import { Container } from "react-bootstrap";
import { observer } from "mobx-react-lite";

import HomePage from "../components/HomePage";
import AboutUs from "../components/AboutUs";
import CategoryList from "../components/CategoryList";
import {fetchCategories, fetchProcedures} from "../http/deviceAPI";
import './stylepages.css';

const Shop = observer(() => {

    const {category} = useContext(Context)

    useEffect(() => {
        fetchTypes().then(data => category.setTypes(data))
        fetchBrands().then(data => category.setBrands(data))
        fetchDevices(null, null, 1, 2).then(data => {
            category.setDevices(data.rows)
            category.setTotalCount(data.count)
        })
    }, [])

    useEffect(() => {
        fetchDevices(category.selectedType.id, category.selectedBrand.id, category.page, 2).then(data => {
            category.setDevices(data.rows)
            category.setTotalCount(data.count)
        })
    }, [category.page, category.selectedType, category.selectedBrand,])

    return (
        <Container>
           <HomePage/>
           <AboutUs/>
           <CategoryList/>
        </Container>
    );
});

export default Shop;
