import React, { useState, useEffect } from 'react';
import Modal from 'react-bootstrap/Modal';
import { Button, Form } from 'react-bootstrap';
import { updateProcedure } from '../../../http/deviceAPI'; 

const EditProcedure = ({ show, onHide, onUpdateProcedures, procedure }) => {
    const [name, setName] = useState(procedure ? procedure.name : '');
    const [description, setDescription] = useState(procedure ? procedure.description : '');
    const [price, setPrice] = useState(procedure ? procedure.price : '');
    const [category, setCategory] = useState(procedure ? procedure.category_id : '');
    const [error, setError] = useState(null);
    const [isModified, setIsModified] = useState(false);

    useEffect(() => {
        setIsModified(false);
    }, [show]);

    const handleUpdate = async () => {
        if (!name || !description || !price || !category) {
            setError('Будь ласка, заповніть усі поля');
            return;
        }

        const updatedProcedure = {
            ...procedure,
            name: name,
            description: description,
            price: price,
            category_id: category
        };

        try {
            await updateProcedure(procedure.procedure_id, updatedProcedure);
            onUpdateProcedures();
            onHide();
        } catch (error) {
            setError(error.message);
        }
    };

    const handleNameChange = (e) => {
        setName(e.target.value);
        setIsModified(true);
    };

    const handleDescriptionChange = (e) => {
        setDescription(e.target.value);
        setIsModified(true);
    };

    const handlePriceChange = (e) => {
        setPrice(e.target.value);
        setIsModified(true);
    };

    const handleCategoryChange = (e) => {
        setCategory(e.target.value);
        setIsModified(true);
    };

    return (
        <Modal show={show} onHide={onHide} centered>
            <Modal.Header closeButton>
                <Modal.Title>Редагувати процедуру</Modal.Title>
            </Modal.Header>
            <Modal.Body>
                <Form>
                    <Form.Group controlId="formProcedureName">
                        <Form.Label>Назва</Form.Label>
                        <Form.Control
                            type="text"
                            placeholder="Введіть назву процедури"
                            value={name || (procedure && procedure.name) || ''}
                            onChange={handleNameChange}
                        />
                    </Form.Group>
                    <Form.Group controlId="formProcedureDescription">
                        <Form.Label>Опис</Form.Label>
                        <Form.Control
                            type="text"
                            placeholder="Введіть опис процедури"
                            value={description || (procedure && procedure.description) || ''}
                            onChange={handleDescriptionChange}
                        />
                    </Form.Group>
                    <Form.Group controlId="formProcedurePrice">
                        <Form.Label>Вартість</Form.Label>
                        <Form.Control
                            type="text"
                            placeholder="Введіть вартість процедури"
                            value={price || (procedure && procedure.price) || ''}
                            onChange={handlePriceChange}
                        />
                    </Form.Group>
                    <Form.Group controlId="formProcedureCategory">
                        <Form.Label>Категорія</Form.Label>
                        <Form.Control
                            as="select"
                            value={category || (procedure && procedure.category_id) || ''}
                            onChange={handleCategoryChange}
                        >
                            {/* Здесь нужно добавить опции для выбора категории */}
                        </Form.Control>
                    </Form.Group>
                </Form>
                {error && <p style={{ color: 'red' }}>{error}</p>}
            </Modal.Body>
            <Modal.Footer>
                <Button variant="secondary" onClick={onHide}>
                    Скасувати
                </Button>
                <Button variant="primary" onClick={handleUpdate} disabled={!isModified}>
                    Зберегти
                </Button>
            </Modal.Footer>
        </Modal>
    );
};

export default EditProcedure;
