import React, { useContext, useEffect } from 'react';
import { Container, Row, Col, Image, Button } from 'react-bootstrap';
import { observer } from 'mobx-react-lite';
import './stylepages.css';
import ButtonGroup from './ButtonGroup';
import ProfileImg from './img/homeimg.png';
import fetchOneUser from "../http/deviceAPI";

// Импортируем контекст
import { Context } from '../index';

const Profile = observer(() => {
    const { user, fetchOneUser } = useContext(Context); // Добавили fetchOneUser для получения данных пользователя

    useEffect(() => {
        // Вызываем метод загрузки профиля при загрузке компонента
        const loadUserProfile = async () => {
            const userData = await fetchOneUser(user.id); // Получаем данные пользователя
            user.setUserProfile(userData); // Обновляем данные пользователя в контексте
        };

        loadUserProfile();
    }, [user, fetchOneUser]);

    return (
        <Container className="profile-container">
            <ButtonGroup />
            <Col className="u-prof-container">
                <Row className="profile-row">
                    <Col xs={12} md={4}>
                        <Image src={ProfileImg} roundedCircle fluid />
                    </Col>
                    <Col xs={12} md={8}>
                        <h2>{user.name} {user.surname}</h2> {/* Обращаемся к полям имени и фамилии из объекта profile */}
                        <Col className="profile-p">
                            <div>
                                <p>{user.phone}</p> {/* Обращаемся к полю телефона из объекта profile */}
                                <p>{user.email}</p> {/* Обращаемся к полю почты из объекта profile */}
                            </div>
                        </Col>
                    </Col>
                </Row>
                <Row>
                    <Col>
                        <p className='ml-3'>Опис.</p>
                        <Button className="prof-button">Редагувати</Button>
                    </Col>
                </Row>
            </Col>
        </Container>
    );
});

export default Profile;
