import React, { useState } from 'react';
import { Button, Form } from 'react-bootstrap';
import { createManager } from '../../../http/deviceAPI'; 

const CreateManager = () => {
    const [managerData, setManagerData] = useState({
        name: '',
        password: '',
        phone: '',
        age: '',
        email: '',
        surname: ''
    });
    const [error, setError] = useState(null);

    const handleChange = (e) => {
        const { name, value } = e.target;
        setManagerData({ ...managerData, [name]: value });
    };

    const handleSubmit = async (e) => {
        e.preventDefault();
        try {
            await createManager(managerData);
            setManagerData({
                name: '',
                password: '',
                phone: '',
                age: '',
                email: '',
                surname: ''
            });
            setError(null);
            alert('Manager created successfully!');
        } catch (error) {
            setError('Failed to create manager. Please try again.');
            console.error('Error creating manager:', error);
        }
    };

    return (
        <Form onSubmit={handleSubmit}>
            <Form.Group controlId="formManagerName">
                <Form.Label>Name</Form.Label>
                <Form.Control
                    type="text"
                    placeholder="Enter name"
                    name="name"
                    value={managerData.name}
                    onChange={handleChange}
                />
            </Form.Group>
            <Form.Group controlId="formManagerPassword">
                <Form.Label>Password</Form.Label>
                <Form.Control
                    type="password"
                    placeholder="Enter password"
                    name="password"
                    value={managerData.password}
                    onChange={handleChange}
                />
            </Form.Group>
            <Form.Group controlId="formManagerPhone">
                <Form.Label>Phone</Form.Label>
                <Form.Control
                    type="text"
                    placeholder="Enter phone"
                    name="phone"
                    value={managerData.phone}
                    onChange={handleChange}
                />
            </Form.Group>
            <Form.Group controlId="formManagerAge">
                <Form.Label>Age</Form.Label>
                <Form.Control
                    type="text"
                    placeholder="Enter age"
                    name="age"
                    value={managerData.age}
                    onChange={handleChange}
                />
            </Form.Group>
            <Form.Group controlId="formManagerEmail">
                <Form.Label>Email</Form.Label>
                <Form.Control
                    type="email"
                    placeholder="Enter email"
                    name="email"
                    value={managerData.email}
                    onChange={handleChange}
                />
            </Form.Group>
            <Form.Group controlId="formManagerSurname">
                <Form.Label>Surname</Form.Label>
                <Form.Control
                    type="text"
                    placeholder="Enter surname"
                    name="surname"
                    value={managerData.surname}
                    onChange={handleChange}
                />
            </Form.Group>
            <Button variant="primary" type="submit">
                Create Manager
            </Button>
            {error && <p style={{ color: 'red' }}>{error}</p>}
        </Form>
    );
};

export default CreateManager;
