import React, { useState, useEffect } from 'react';
import { observer } from 'mobx-react';
import { Button, Container, Table, Spinner } from "react-bootstrap";
import CreateCategory from "../components/modals/CreateCategory";
import EditCategory from "../components/modals/EditCategory"; // Import the modal for editing
import { fetchCategories as fetchCategoriesAPI, createCategory, updateCategory } from "../http/deviceAPI"; // Add the updateCategory function
import './stylepages.css';

const AdmCategory = observer(() => {
    const [categoryVisible, setCategoryVisible] = useState(false);
    const [editVisible, setEditVisible] = useState(false); // State for edit modal visibility
    const [categories, setCategories] = useState([]);
    const [loading, setLoading] = useState(true);
    const [error, setError] = useState(null);
    const [selectedCategory, setSelectedCategory] = useState(null); // State to store the selected category for editing

    const handleUpdateCategories = async () => {
        try {
            const response = await fetchCategoriesAPI();
            setCategories(response);
        } catch (error) {
            console.error('Error fetching categories:', error);
            setError('Failed to fetch categories. Please try again.');
        }
    };

    useEffect(() => {
        fetchCategories();
    }, []);

    const fetchCategories = async () => {
        try {
            const response = await fetchCategoriesAPI();
            setCategories(response);
            setLoading(false);
        } catch (error) {
            console.error('Error fetching categories:', error);
            setError('Failed to fetch categories. Please try again.');
            setLoading(false);
        }
    };

    const addCategory = (categoryData) => {
        createCategory(categoryData)
            .then(() => {
                fetchCategories();
                setCategoryVisible(false);
            })
            .catch(error => {
                setError('Сталася помилка під час додавання категорії');
                console.error('Помилка під час додавання категорії:', error);
            });
    };

    const handleEdit = (category) => {
        setSelectedCategory(category);
        setEditVisible(true);
    };

    const handleDelete = (id) => {
        // Handle delete action
        console.log("Delete category with ID:", id);
    };

    const handleUpdateCategory = (updatedCategory) => {
        updateCategory(updatedCategory)
            .then(() => {
                fetchCategories();
                setEditVisible(false);
            })
            .catch(error => {
                setError('Сталася помилка під час оновлення категорії');
                console.error('Помилка під час оновлення категорії:', error);
            });
    };

    return (
        <Container className="d-flex flex-column">
            {loading ? (
                <Spinner animation="border" role="status">
                    <span className="sr-only">Завантаження...</span>
                </Spinner>
            ) : error ? (
                <p>{error}</p>
            ) : (
                <>
                    <Table striped bordered hover className="mt-4">
                        <thead>
                            <tr>
                                <th>Категорія</th>
                                <th>ID</th>
                                <th>Дії</th>
                            </tr>
                        </thead>
                        <tbody>
                            {categories.map(category => (
                                <tr key={category.id}>
                                    <td>{category.name}</td>
                                    <td>{category.id}</td>
                                    <td>
                                        <Button variant="primary" onClick={() => handleEdit(category)}>Редагувати</Button>{' '}
                                        <Button variant="danger" onClick={() => handleDelete(category.id)}>Видалити</Button>
                                    </td>
                                </tr>
                            ))}
                        </tbody>
                    </Table>

                    <Button className="add-button" onClick={() => setCategoryVisible(true)} >
                        Додати категорію
                    </Button>
                    <CreateCategory
                        show={categoryVisible}
                        onHide={() => setCategoryVisible(false)}
                        onUpdateCategories={handleUpdateCategories}
                    />
                    <EditCategory
                        show={editVisible}
                        onHide={() => setEditVisible(false)}
                        onUpdateCategory={handleUpdateCategory}
                        category={selectedCategory}
                    />
                </>
            )}
        </Container>
    );
});

export default AdmCategory;
