import React from 'react';
import Logo from './img/Logo.png';
import Call from './img/Call.png';
import Zoom from './img/Zoom.png';

const SalonItem = ({ salon }) => {
  return (
    <div className="salon-item">

      <img src={process.env.REACT_APP_API_URL + salon.img} alt={salon.name} style={{ width: '200px', height: '100px' }} />
      <h7 className='salon-name'>{salon.name}</h7>
      <h1 className='salon-address'>{salon.address}</h1>
      <div>

        <p>Відвідати нас :</p>
        <p>{salon.address}</p>

        <p>Написати нам :</p>
        <p>KRASASUPPORT@GMAIL.COM</p>

        <p>Подзвонити нам :</p>
        <p>+380 (67) 123 - 45 - 67</p>
      </div>

    </div>
  );
};

export default SalonItem;
