import React, { useContext, useEffect } from 'react';
import { Container } from "react-bootstrap";
import { observer } from "mobx-react-lite";
import { Context } from "../index";
import HomePage from "../components/HomePage";
import AboutUs from "../components/AboutUs";
import { fetchCategories, fetchProcedures } from "../http/deviceAPI";
import './stylepages.css';

const Shop = observer(() => {
    const { device } = useContext(Context);

    useEffect(() => {
        // Fetch categories and procedures
        Promise.all([fetchCategories(), fetchProcedures()])
            .then(([categoriesData, proceduresData]) => {
                device.setCategories(categoriesData);
                device.setProcedures(proceduresData);
            })
            .catch(error => {
                // Handle error
                console.error('Error fetching data:', error);
            });

        // Fetch devices with default parameters
        fetchDevices(null, null, 1, 2)
            .then(data => {
                device.setDevices(data.rows);
                device.setTotalCount(data.count);
            })
            .catch(error => {
                // Handle error
                console.error('Error fetching devices:', error);
            });
    }, []);

    useEffect(() => {
        // Fetch devices with selected category and procedure
        fetchDevices(device.selectedCategory.id, device.selectedProcedure.id, device.page, 2)
            .then(data => {
                device.setDevices(data.rows);
                device.setTotalCount(data.count);
            })
            .catch(error => {
                // Handle error
                console.error('Error fetching devices:', error);
            });
    }, [device.page, device.selectedCategory, device.selectedProcedure]);

    return (
        <Container>
            <HomePage/>
            <AboutUs/>
            {/* Render your other components */}
        </Container>
    );
});

export default Shop;
