import React, {useContext, useEffect} from 'react';
import { Container, Row, Col, Image, Button } from "react-bootstrap";
import { observer } from "mobx-react-lite";
import { Context } from "../index";
import TypeBar from "../components/TypeBar";
import BrandBar from "../components/BrandBar";
import DeviceList from "../components/DeviceList";
import {fetchBrands, fetchDevices, fetchTypes} from "../http/deviceAPI";
import Pages from "../components/Pages";
import './stylepages.css';

const Shop = observer(() => {
    const {device} = useContext(Context)

    useEffect(() => {
        fetchTypes().then(data => device.setTypes(data))
        fetchBrands().then(data => device.setBrands(data))
        fetchDevices(null, null, 1, 2).then(data => {
            device.setDevices(data.rows)
            device.setTotalCount(data.count)
        })
    }, [])

    useEffect(() => {
        fetchDevices(device.selectedType.id, device.selectedBrand.id, device.page, 2).then(data => {
            device.setDevices(data.rows)
            device.setTotalCount(data.count)
        })
    }, [device.page, device.selectedType, device.selectedBrand,])

    return (
        <Container className="home-container">
            <Row className="home-row">
                <Col className="home-col-l">
                    <h1>Краса - це сила, а посмішка - її меч.</h1>
                    <p className="home-p">Місце спілкування унікальних жінок, ідеальне місце тв перегляду краси світу!</p>
                    <Button className="home-button">ЗАРЕЄСТРУВАТИСЯ НА ПРОГРАМУ</Button>
                </Col>
                <Col className="home-col-r">
                    <Image className="home-img" src="cosmetic-tube.jpg" fluid />
                </Col>
            </Row>
        </Container>
    );
});

export default Shop;
