import React, { useContext } from 'react';
import { observer } from 'mobx-react-lite';
import { Context } from '../index';
import { Button, Card, Container, Image } from 'react-bootstrap';

const Basket = observer(() => {
  const { basket } = useContext(Context);

  return (
    <Container className="d-flex flex-column">
      {basket.basketItems.map((item) => (
        <Card key={item.device && item.device.id} className="mt-3">
          <Card.Body className="d-flex">
            {item.device && (
              <Image
                src={process.env.REACT_APP_API_URL + item.device.img}
                width={100}
                height={100}
                className="mr-3"
              />
            )}
            <div>
              {item.device && item.device.name && (
                <Card.Title>{item.device.name}</Card.Title>
              )}
              {item.device && item.device.price && (
                <Card.Text>Ціна: {item.device.price} грн</Card.Text>
              )}
              <Card.Text>Кількість: {item.quantity}</Card.Text>
            </div>
          </Card.Body>
        </Card>
      ))}
      <Button variant="outline-danger" className="mt-3">
        Очистити корзину
      </Button>
    </Container>
  );
});

export default Basket;
