import React from 'react';
import { useHistory } from 'react-router-dom'; // Импортируем useHistory из react-router-dom

const CategoryItem = ({ category }) => {
  const history = useHistory(); 

  const handleCategoryClick = () => {
    history.push(`/salon/${category.category_id}`);
  };

  return (
    <div className="category-item" onClick={handleCategoryClick}>
      <img src={process.env.REACT_APP_API_URL + category.img} alt={category.name} style={{ width: '200px', height: '100px' }} />
      <p>{category.name}</p>
    </div>
  );
};

export default CategoryItem;
