import React, { useContext, useEffect } from 'react';
import { Container } from "react-bootstrap";
import { observer } from "mobx-react-lite";
import { Context } from "../index";
import HomePage from "../components/HomePage";
import AboutUs from "../components/AboutUs";
import { fetchCategories, fetchProcedures, fetchSalons } from "../http/deviceAPI";
import './stylepages.css';

const Salon = observer(() => {
    const { procedure } = useContext(Context);

    useEffect(() => {
        fetchProcedureCategories().then(data => procedure.setCategories(data));
        fetchProcedures(null, null, 1, 2).then(data => {
            procedure.setProcedures(data.rows);
            procedure.setTotalCount(data.count);
        });
    }, [procedure]); // Включаем procedure в массив зависимостей

    useEffect(() => {
        fetchProcedures(procedure.selectedCategory.id, null, procedure.page, 2).then(data => {
            procedure.setProcedures(data.rows);
            procedure.setTotalCount(data.count);
        });
    }, [procedure.page, procedure.selectedCategory]); // Включаем в массив зависимостей нужные переменные

    return (
        <Container>
           <HomePage/>
           <AboutUs/>
        </Container>
    );
});

export default Salon;
