// CreateMaster.js
import React, { useState } from 'react';
import Modal from 'react-bootstrap/Modal';
import { Button, Form } from 'react-bootstrap';
import { createMaster } from '../../../http/deviceAPI'; // Assuming this function is imported from the appropriate API file

const CreateMaster = ({ show, onHide, onUpdateMasters }) => {
    const [name, setName] = useState('');
    const [password, setPassword] = useState('');
    const [phone, setPhone] = useState('');
    const [age, setAge] = useState('');
    const [email, setEmail] = useState('');
    const [surname, setSurname] = useState('');
    const [error, setError] = useState(null);
    const [loading, setLoading] = useState(false);

    const addMaster = async () => {
        if (!name || !password || !phone || !age || !email || !surname) {
            setError('Please fill in all fields');
            return;
        }

        setLoading(true); // Set loading state to true while the request is in progress

        try {
            await createMaster({ name, password, phone, age, email, surname });
            onUpdateMasters(); // Call the callback function to update the master list
            setName('');
            setPassword('');
            setPhone('');
            setAge('');
            setEmail('');
            setSurname('');
            setError(null);
            onHide();
        } catch (error) {
            setError('An error occurred while adding the master');
            console.error('Error adding master:', error);
        } finally {
            setLoading(false); // Reset loading state whether the request succeeds or fails
        }
    };

    return (
        <Modal show={show} onHide={onHide} centered>
            <Modal.Header closeButton>
                <Modal.Title>Add Master</Modal.Title>
            </Modal.Header>
            <Modal.Body>
                <Form>
                    {/* Your form inputs */}
                </Form>
                {error && <p style={{ color: 'red' }}>{error}</p>}
            </Modal.Body>
            <Modal.Footer>
                <Button variant="secondary" onClick={onHide} disabled={loading}>
                    Cancel
                </Button>
                <Button variant="primary" onClick={addMaster} disabled={loading}>
                    {loading ? 'Saving...' : 'Save'}
                </Button>
            </Modal.Footer>
        </Modal>
    );
};

export default CreateMaster;
