import React, { useContext, useEffect } from 'react';
import { Container, Row, Col, Image, Button } from 'react-bootstrap';
import { observer } from 'mobx-react-lite';
import './stylepages.css';
import ButtonGroup from './ButtonGroup';
import ProfileImg from './img/homeimg.png';

// Импортируем контекст
import { Context } from '../index';

const Profile = observer(() => {
  const { user } = useContext(Context);

  // Получаем данные пользователя при загрузке страницы
  useEffect(() => {
    // Предположим, что есть функция для загрузки данных о пользователе
    // Например, user.loadProfile();
  }, []);

  return (
    <Container className="profile-container">
      <ButtonGroup />
      <Col className="u-prof-container">
        <Row className="profile-row">
          <Col xs={12} md={4}>
            <Image src={ProfileImg} roundedCircle fluid />
          </Col>
          <Col xs={12} md={8}>
            <h2>{user.name} {user.surname}</h2>
            <Col className="profile-p">
              <div>
                <p>{user.phone}</p>
                <p>{user.email}</p>
              </div>
            </Col>
          </Col>
        </Row>
        <Row>
          <Col>
            <p className='ml-3'>Опис.</p>
            <Button className="prof-button">Редагувати</Button>
          </Col>
        </Row>
      </Col>
    </Container>
  );
});

export default Profile;
