import React, {useContext, useEffect} from 'react';     
import { Container } from "react-bootstrap";
import { observer } from "mobx-react-lite";
import {Context} from "../index";
import CategoryList from "../components/CategoryList"; // Импорт компонента CategoryList
import {fetchCategories, } from "../http/deviceAPI";
import HomePage from "../components/HomePage";
import AboutUs from "../components/AboutUs";

const Home = observer(() => {
    // Инициализируем категории в контексте перед использованием компонента CategoryList
    const { procedureSalon } = useContext(Context);
    useEffect(() => {
        // Загружаем категории из API или любого другого источника данных
        fetchCategories().then(categories => procedureSalon.setCategories(categories));
    }, []);

    return (
        <Container>
            <HomePage/>
            <CategoryList /> {/* Вставляем компонент CategoryList */}
            <AboutUs/>
        </Container>
    );
});

export default Home;
