import React, { useState, useEffect } from 'react';
import { observer } from 'mobx-react';
import Modal from 'react-bootstrap/Modal';
import { Button, Container, Table, Spinner } from "react-bootstrap";
import { PencilSquare, Trash } from 'react-bootstrap-icons'; 
import CreateClient from "../components/modals/clients/CreateClient";
import EditClient from "../components/modals/clients/EditClient";
import { fetchClients as fetchClientsAPI, deleteClient } from "../http/deviceAPI"; // Import fetchClients and deleteClient functions from appropriate API
import './stylepages.css';

const AdmClient = observer(() => {
    const [clientVisible, setClientVisible] = useState(false);
    const [editVisible, setEditVisible] = useState(false);
    const [deleteVisible, setDeleteVisible] = useState(false);
    const [clients, setClients] = useState([]);
    const [loading, setLoading] = useState(true);
    const [error, setError] = useState(null);
    const [selectedClient, setSelectedClient] = useState(null);

    const fetchClients = async () => {
        try {
            const response = await fetchClientsAPI();
            setClients(response);
            setLoading(false);
        } catch (error) {
            console.error('Помилка отримання клієнтів:', error);
            setError('Не вдалося залучити клієнтів. Будь ласка, спробуйте ще раз.');
            setLoading(false);
        }
    };

    const handleEdit = (client) => {
        setSelectedClient(client);
        setEditVisible(true);
    };

    const handleDelete = (client) => {
        setSelectedClient(client);
        setDeleteVisible(true);
    };

    const confirmDelete = async () => {
        try {
            await deleteClient(selectedClient.client_id);
            fetchClients();
            setDeleteVisible(false);
        } catch (error) {
            console.error('Помилка отримання клієнтів:', error);
            setError('Не вдалося залучити клієнтів. Будь ласка, спробуйте ще раз.');
        }
    };

    useEffect(() => {
        fetchClients();
    }, []);

    return (
        <Container className="d-flex flex-column">
            {loading ? (
                <Spinner animation="border" role="status">
                    <span className="sr-only">Loading...</span>
                </Spinner>
            ) : error ? (
                <p>{error}</p>
            ) : (
                <>
                    <Table striped bordered hover className="mt-4">
                        <thead>
                            <tr>
                                <th>Name</th>
                                <th>Surname</th>
                                <th>Email</th>
                                <th>Age</th>
                                <th>Phone</th>
                                <th>Actions</th>
                            </tr>
                        </thead>
                        <tbody>
                            {clients.map(client => (
                                <tr key={client.client_id}>
                                    <td>{client.name}</td>
                                    <td>{client.surname}</td>
                                    <td>{client.email}</td>
                                    <td>{client.age}</td>
                                    <td>{client.phone}</td>
                                    <td>
                                        <Button className='edit-button' onClick={() => handleEdit(client)}><PencilSquare /></Button>{' '}
                                        <Button className='delete-button' onClick={() => handleDelete(client)}><Trash /></Button>
                                    </td>
                                </tr>
                            ))}
                        </tbody>
                    </Table>

                    <Button className="add-button" onClick={() => setClientVisible(true)} >
                        Add Client
                    </Button>
                    <CreateClient
                        show={clientVisible}
                        onHide={() => setClientVisible(false)}
                        onUpdateClients={fetchClients}
                    />
                    <EditClient
                        show={editVisible}
                        onHide={() => setEditVisible(false)}
                        onUpdateClient={fetchClients}
                        client={selectedClient}
                    />
                    <Modal show={deleteVisible} onHide={() => setDeleteVisible(false)} centered>
                        <Modal.Header closeButton>
                            <Modal.Title>Confirm Delete</Modal.Title>
                        </Modal.Header>
                        <Modal.Body>
                            <p>Delete client?</p>
                        </Modal.Body>
                        <Modal.Footer>
                            <Button variant="secondary" onClick={() => setDeleteVisible(false)}>
                                Cancel
                            </Button>
                            <Button variant="danger" onClick={confirmDelete}>
                                Delete
                            </Button>
                        </Modal.Footer>
                    </Modal>
                </>
            )}
        </Container>
    );
});

export default AdmClient;
