import React, { useState, useEffect } from 'react';
import { observer } from 'mobx-react';
import { Button, Container, Table, Spinner, Modal, Form } from "react-bootstrap";
import { PencilSquare, Trash } from 'react-bootstrap-icons';
import CreateMaster from "../components/modals/masters/CreateMaster";
import EditMaster from "../components/modals/masters/EditMaster";
import { fetchMasters as fetchMastersAPI, deleteMaster } from "../http/deviceAPI"; // Импорт функций для работы с мастерами
import './stylepages.css';

const AdmMaster = observer(() => {
    const [masterVisible, setMasterVisible] = useState(false);
    const [editVisible, setEditVisible] = useState(false);
    const [deleteVisible, setDeleteVisible] = useState(false);
    const [masters, setMasters] = useState([]);
    const [loading, setLoading] = useState(true);
    const [error, setError] = useState(null);
    const [selectedMaster, setSelectedMaster] = useState(null);
    const [searchQuery, setSearchQuery] = useState("");

    const fetchMasters = async () => {
        try {
            const response = await fetchMastersAPI();
            setMasters(response);
            setLoading(false);
        } catch (error) {
            console.error('Помилка отримання майстрів:', error);
            setError('Не вдалося отримати список майстрів. Спробуйте ще раз.');
            setLoading(false);
        }
    };

    const handleEdit = (master) => {
        setSelectedMaster(master);
        setEditVisible(true);
    };

    const handleDelete = (master) => {
        setSelectedMaster(master);
        setDeleteVisible(true);
    };

    const confirmDelete = async () => {
        try {
            await deleteMaster(selectedMaster.master_id);
            fetchMasters();
            setDeleteVisible(false);
        } catch (error) {
            console.error('Помилка видалення майстра:', error);
            setError('Помилка видалення майстра. Спробуйте ще раз.');
        }
    };

    useEffect(() => {
        fetchMasters();
    }, []);

    const handleSearchChange = (event) => {
        setSearchQuery(event.target.value);
    };

    const filteredMasters = masters.filter(master => {
        return (
            master.name.toLowerCase().includes(searchQuery.toLowerCase()) ||
            master.surname.toLowerCase().includes(searchQuery.toLowerCase()) ||
            master.phone.toLowerCase().includes(searchQuery.toLowerCase()) ||
            master.email.toLowerCase().includes(searchQuery.toLowerCase())
        );
    });

    return (
        <Container className="d-flex flex-column">
            {loading ? (
                <Spinner animation="border" role="status">
                    <span className="sr-only">Завантаження...</span>
                </Spinner>
            ) : error ? (
                <p>{error}</p>
            ) : (
                <>
                    <Form.Control
                        type="text"
                        placeholder="Пошук..."
                        value={searchQuery}
                        onChange={handleSearchChange}
                    />

                    <Table striped bordered hover className="mt-4">
                        <thead>
                            <tr>
                                <th>ID</th>
                                <th>Ім'я</th>
                                <th>Прізвище</th>
                                <th>Телефон</th>
                                <th>Пошта</th>
                                <th>Вік</th>
                                <th>Дії</th>
                            </tr>
                        </thead>
                        <tbody>
                            {filteredMasters.map(master => (
                                <tr key={master.master_id}>
                                    <td>{master.master_id}</td>
                                    <td>{master.name}</td>
                                    <td>{master.surname}</td>
                                    <td>{master.phone}</td>
                                    <td>{master.email}</td>
                                    <td>{master.age}</td>
                                    <td>
                                        <Button className='edit-button' onClick={() => handleEdit(master)}><PencilSquare /></Button>{' '}
                                        <Button className='delete-button' onClick={() => handleDelete(master)}><Trash /></Button>
                                    </td>
                                </tr>
                            ))}
                        </tbody>
                    </Table>

                    <Button className="add-button" onClick={() => setMasterVisible(true)} >
                        Додати майстра
                    </Button>
                    <CreateMaster
                        show={masterVisible}
                        onHide={() => setMasterVisible(false)}
                        onUpdateMasters={fetchMasters}
                    />
                    <EditMaster
                        show={editVisible}
                        onHide={() => setEditVisible(false)}
                        onUpdateMaster={fetchMasters}
                        master={selectedMaster}
                    />
                    <Modal show={deleteVisible} onHide={() => setDeleteVisible(false)} centered>
                        <Modal.Header closeButton>
                            <Modal.Title>Підтвердити видалення</Modal.Title>
                        </Modal.Header>
                        <Modal.Body>
                            <p>Видалити майстра?</p>
                        </Modal.Body>
                        <Modal.Footer>
                            <Button className='btn-cancel' onClick={() => setDeleteVisible(false)}>
                                Скасувати
                            </Button>
                            <Button className='btn-save' onClick={confirmDelete}>
                                Видалити
                            </Button>
                        </Modal.Footer>
                    </Modal>
                </>
            )}
        </Container>
    );
});

export default AdmMaster;
