import { Container } from "react-bootstrap";
import { observer } from "mobx-react-lite";

import HomePage from "../components/HomePage";
import AboutUs from "../components/AboutUs";
import CategoryItem from "../components/CategoryItem";

import {fetchCategories, fetchProcedures} from "../http/deviceAPI";
import './stylepages.css';

const Shop = observer(() => {
    const {category} = useContext(Context)

    useEffect(() => {
        //fetchTypes().then(data => device.setTypes(data))
        //fetchBrands().then(data => device.setBrands(data))
        fetchCategories(null, null, 1, 2).then(data => {
            category.setCategory(data.rows)
            //category.setTotalCount(data.count)
        })
    }, [])

    useEffect(() => {
        fetchCategories(category.page, 2).then(data => {
            category.setCategory(data.rows)
        })
    }, [category.page])

    return (
        <Container>
           <HomePage/>
           <AboutUs/>
           <CategoryItem/>
        </Container>
    );
});

export default Shop;
