import React, { useState, useEffect } from 'react';
import { observer } from 'mobx-react';
import { Button, Container, Table, Spinner } from "react-bootstrap";
import CreateCategory from "../components/modals/CreateCategory";
import { fetchCategories as fetchCategoriesAPI, createCategory } from "../http/deviceAPI"; // добавим функцию создания категории
import './stylepages.css';

const AdmCategory = observer(() => {
    const [categoryVisible, setCategoryVisible] = useState(false);
    const [categories, setCategories] = useState([]);
    const [loading, setLoading] = useState(true);
    const [error, setError] = useState(null);

    const handleUpdateCategories = async () => {
        try {
            const response = await fetchCategoriesAPI();
            setCategories(response);
        } catch (error) {
            console.error('Error fetching categories:', error);
            setError('Failed to fetch categories. Please try again.');
        }
    };

    useEffect(() => {
        fetchCategories();
    }, []);

    const fetchCategories = async () => {
        try {
            const response = await fetchCategoriesAPI();
            setCategories(response);
            setLoading(false);
        } catch (error) {
            console.error('Error fetching categories:', error);
            setError('Failed to fetch categories. Please try again.');
            setLoading(false);
        }
    };

    const addCategory = (categoryData) => {
        createCategory(categoryData)
            .then(() => {
                // После успешного добавления категории обновляем список
                fetchCategories();
                setCategoryVisible(false); // Закрываем модальное окно
            })
            .catch(error => {
                setError('Сталася помилка під час додавання категорії');
                console.error('Помилка під час додавання категорії:', error);
            });
    };

    return (
        <Container className="d-flex flex-column">
            {loading ? (
                <Spinner animation="border" role="status">
                    <span className="sr-only">Завантаження...</span>
                </Spinner>
            ) : error ? (
                <p>{error}</p>
            ) : (
                <>
                    <Table striped bordered hover className="mt-4">
                        <thead>
                            <tr>
                                <th>Категорія</th>
                                <th>ID</th>
                            </tr>
                        </thead>
                        <tbody>
                            {categories.map(category => (
                                <tr key={category.id}>
                                    <td>{category.name}</td>
                                    <td>{category.id}</td>
                                </tr>
                            ))}
                        </tbody>
                    </Table>

                    <Button className="add-button" onClick={() => setCategoryVisible(true)} >
                        Додати категорію
                    </Button>
                    <CreateCategory
                        show={categoryVisible}
                        onHide={() => setCategoryVisible(false)}
                        onUpdateCategories={handleUpdateCategories} // Pass the callback function
                    />
                </>
            )}
        </Container>
    );
});

export default AdmCategory;
