import React, { useState } from 'react';
import Modal from 'react-bootstrap/Modal';
import { Button, Form } from 'react-bootstrap';
import { createSalon } from '../../../http/deviceAPI'; // Assuming API function for creating salon

const CreateSalon = ({ show, onHide, onSalonCreated }) => {
    const [name, setName] = useState('');
    const [address, setAddress] = useState('');
    const [description, setDescription] = useState('');
    const [error, setError] = useState(null);

    const handleCreateSalon = () => {
        if (!name || !address || !description) {
            setError('Please fill in all fields.');
            return;
        }

        createSalon({ name, address, description })
            .then(() => {
                setName('');
                setAddress('');
                setDescription('');
                setError(null);
                onHide(); // Move onHide here
                onSalonCreated(); // Callback to refresh salon list after creation
            })
            .catch((error) => {
                setError('Error creating salon. Please try again.');
                console.error('Error creating salon:', error);
            });
    };

    return (
        <Modal show={show} onHide={onHide} centered>
            <Modal.Header closeButton>
                <Modal.Title>Додати Салон</Modal.Title>
            </Modal.Header>
            <Modal.Body>
                <Form>
                    <Form.Group controlId="formSalonName">
                        <Form.Label>Назва</Form.Label>
                        <Form.Control
                            type="text"
                            placeholder="Введіть назву салону"
                            value={name}
                            onChange={(e) => setName(e.target.value)}
                        />
                    </Form.Group>
                    <Form.Group controlId="formSalonAddress">
                        <Form.Label>Адреса</Form.Label>
                        <Form.Control
                            type="text"
                            placeholder="Введіть адресу салона"
                            value={address}
                            onChange={(e) => setAddress(e.target.value)}
                        />
                    </Form.Group>
                    <Form.Group controlId="formSalonDescription">
                        <Form.Label>Опис</Form.Label>
                        <Form.Control
                            type="text"
                            placeholder="Введіть опис салону"
                            value={description}
                            onChange={(e) => setDescription(e.target.value)}
                        />
                    </Form.Group>
                </Form>
                {error && <p style={{ color: 'red' }}>{error}</p>}
            </Modal.Body>
            <Modal.Footer>
                <Button className='btn-cancel' onClick={onHide}>
                    Скасувати
                </Button>
                <Button className='btn-save' onClick={handleCreateSalon}>
                    Зберегти
                </Button>
            </Modal.Footer>
        </Modal>
    );
};

export default CreateSalon;
