import { makeAutoObservable } from 'mobx';

export default class BasketStore {
  basketItems = [];

  constructor() {
    makeAutoObservable(this);
  }

  addToBasket(item) {
    const existingItem = this.basketItems.find((basketItem) => basketItem.id === item.id);

    if (existingItem) {
      existingItem.quantity += 1; // Increase the quantity if the item already exists in the basket
    } else {
      item.quantity = 1; // Set the initial quantity to 1 for new items
      this.basketItems.push(item);
    }
  }

  clearBasket() {
    this.basketItems = [];
  }
}
