// CreateMaster.js
import React, { useState } from 'react';
import Modal from 'react-bootstrap/Modal';
import { Button, Form } from 'react-bootstrap';
import { createMaster } from '../../../http/deviceAPI'; // Assuming this function is imported from the appropriate API file

const CreateMaster = ({ show, onHide, onUpdateMasters }) => {
    const [name, setName] = useState('');
    const [password, setPassword] = useState('');
    const [phone, setPhone] = useState('');
    const [age, setAge] = useState('');
    const [email, setEmail] = useState('');
    const [surname, setSurname] = useState('');
    const [procedures, setProcedures] = useState([]);
    const [selectedProcedures, setSelectedProcedures] = useState([]); // Define selectedProcedures state
    const [error, setError] = useState(null);

    const addMaster = () => {
        if (!name || !password || !phone || !age || !email || !surname) {
            setError('Please fill in all fields');
            return;
        }

        const procedureIds = selectedProcedures; // Получите выбранные процедуры

        createMaster({ name, password, phone, age, email, surname, procedureIds }) // Отправьте выбранные процедуры на сервер
            .then((data) => {
                onUpdateMasters();
                setName('');
                setPassword('');
                setPhone('');
                setAge('');
                setEmail('');
                setSurname('');
                setSelectedProcedures([]); // Очистите выбранные процедуры
                setError(null);
                onHide();
            })
            .catch(error => {
                setError('An error occurred while adding the master');
                console.error('Error adding master:', error);
            });
    };

    useEffect(() => {
        fetchProcedures(); // Загрузите список процедур при монтировании компонента
    }, []);

    const fetchProcedures = async () => {
        try {
            const response = await fetchProceduresFromAPI(); // Замените на соответствующий вызов API для получения списка процедур
            setProcedures(response); // Установите список процедур в состояние
        } catch (error) {
            console.error('Error fetching procedures:', error);
        }
    };

    const handleProcedureSelection = (procedureId) => { // Define handleProcedureSelection function
        if (selectedProcedures.includes(procedureId)) {
            setSelectedProcedures(selectedProcedures.filter(id => id !== procedureId));
        } else {
            setSelectedProcedures([...selectedProcedures, procedureId]);
        }
    };

    return (
        <Modal show={show} onHide={onHide} centered>
            <Modal.Header closeButton>
                <Modal.Title>Додати Майстра</Modal.Title>
            </Modal.Header>
            <Modal.Body>
                <Form>
                    <Form.Group controlId="formMasterName">
                        <Form.Label>Ім'я</Form.Label>
                        <Form.Control
                            type="text"
                            placeholder="Введіть ім'я"
                            value={name}
                            onChange={(e) => setName(e.target.value)}
                        />
                    </Form.Group>
                    <Form.Group controlId="formMasterPassword">
                        <Form.Label>Пароль</Form.Label>
                        <Form.Control
                            type="password"
                            placeholder="Введіть пароль"
                            value={password}
                            onChange={(e) => setPassword(e.target.value)}
                        />
                    </Form.Group>
                    <Form.Group controlId="formMasterPhone">
                        <Form.Label>Телефон</Form.Label>
                        <Form.Control
                            type="text"
                            placeholder="Введіть телефон"
                            value={phone}
                            onChange={(e) => setPhone(e.target.value)}
                        />
                    </Form.Group>
                    <Form.Group controlId="formMasterAge">
                        <Form.Label>Вік</Form.Label>
                        <Form.Control
                            type="text"
                            placeholder="Введіть вік"
                            value={age}
                            onChange={(e) => setAge(e.target.value)}
                        />
                    </Form.Group>
                    <Form.Group controlId="formMasterEmail">
                        <Form.Label>Електронна пошта</Form.Label>
                        <Form.Control
                            type="email"
                            placeholder="Введіть електронну пошту"
                            value={email}
                            onChange={(e) => setEmail(e.target.value)}
                        />
                    </Form.Group>
                    <Form.Group controlId="formMasterSurname">
                        <Form.Label>Прізвище</Form.Label>
                        <Form.Control
                            type="text"
                            placeholder="Введіть прізвище"
                            value={surname}
                            onChange={(e) => setSurname(e.target.value)}
                        />
                    </Form.Group>
                    <Form.Group controlId="formProcedures">
                        <Form.Label>Процедури</Form.Label>
                        {procedures.map(procedure => (
                            <Form.Check
                                key={procedure.procedure_id}
                                type="checkbox"
                                id={`procedure-${procedure.procedure_id}`}
                                label={procedure.name}
                                checked={selectedProcedures.includes(procedure.procedure_id)}
                                onChange={() => handleProcedureSelection(procedure.procedure_id)}
                            />
                        ))}
                    </Form.Group>
                </Form>
                {error && <p style={{ color: 'red' }}>{error}</p>}
            </Modal.Body>
            <Modal.Footer>
                <Button className='btn-cancel' onClick={onHide}>
                    Скасувати
                </Button>
                <Button className='btn-save' onClick={addMaster}>
                    Зберегти
                </Button>
            </Modal.Footer>
        </Modal>
    );
};

export default CreateMaster;
