import React, { useState } from 'react';
import Modal from 'react-bootstrap/Modal';
import { Button, Form } from 'react-bootstrap';
import { updateSalon } from '../../../http/deviceAPI'; // Importing the API function for updating salon

const EditSalon = ({ show, onHide, onUpdateSalon, salon }) => {
    const [name, setName] = useState(salon ? salon.name : '');
    const [address, setAddress] = useState(salon ? salon.address : '');
    const [description, setDescription] = useState(salon ? salon.description : '');
    const [error, setError] = useState(null);

    const handleUpdateSalon = () => {
        if (!name || !address || !description) {
            setError('Please fill in all fields.');
            return;
        }

        const updatedSalon = {
            ...salon,
            name: name,
            address: address,
            description: description
        };

        updateSalon(salon.salon_id, updatedSalon)
            .then(() => {
                onUpdateSalon();
                setName('');
                setAddress('');
                setDescription('');
                setError(null);
                onHide();
            })
            .catch((error) => {
                setError('Error updating salon. Please try again.');
                console.error('Error updating salon:', error);
            });
    };

    return (
        <Modal show={show} onHide={onHide} centered>
            <Modal.Header closeButton>
                <Modal.Title>Редагувати Дані</Modal.Title>
            </Modal.Header>
            <Modal.Body>
                <Form>
                    <Form.Group controlId="formSalonName">
                        <Form.Label>Назва</Form.Label>
                        <Form.Control
                            type="text"
                            placeholder="Введіть назву салону"
                            value={description || (salon && salon.name) || ''}
                            onChange={(e) => setName(e.target.value)}
                        />
                    </Form.Group>
                    <Form.Group controlId="formSalonAddress">
                        <Form.Label>Адреса</Form.Label>
                        <Form.Control
                            type="text"
                            placeholder="Введіть адресу салону"
                            value={description || (salon && salon.address) || ''}
                            onChange={(e) => setAddress(e.target.value)}
                        />
                    </Form.Group>
                    <Form.Group controlId="formSalonDescription">
                        <Form.Label>Опис</Form.Label>
                        <Form.Control
                            type="text"
                            placeholder="Введіть опис салону"
                            value={description || (salon && salon.description) || ''}
                            onChange={(e) => setDescription(e.target.value)}
                        />
                    </Form.Group>
                </Form>
                {error && <p style={{ color: 'red' }}>{error}</p>}
            </Modal.Body>
            <Modal.Footer>
                <Button className='btn-cancel' onClick={onHide}>
                    Скасувати
                </Button>
                <Button className='btn-save' onClick={handleUpdateSalon}>
                    Зберегти
                </Button>
            </Modal.Footer>
        </Modal>
    );
};

export default EditSalon;
