import React, { useState, useEffect } from 'react';
import { observer } from 'mobx-react';
import { Button, Container, Table, Spinner } from "react-bootstrap";
import CreateProcedure from "../components/modals/CreateProcedure";
import { fetchProcedures } from "../http/deviceAPI";
import './stylepages.css';

const AdmProcedure = observer(() => {
    const [procedureVisible, setProcedureVisible] = useState(false);
    const [procedures, setProcedures] = useState([]);
    const [loading, setLoading] = useState(true);
    const [error, setError] = useState(null);

    const handleUpdateProcedures = async () => {
        try {
            const response = await fetchProcedures();
            setProcedures(response);
        } catch (error) {
            console.error('Помилка отримання процедури:', error);
            setError('Не вдалося отримати процедури. Спробуйте ще раз.');
        }
    };

    useEffect(() => {
        async function fetchProceduresData() {
            try {
                const response = await fetchProcedures();
                setProcedures(response);
                setLoading(false);
            } catch (error) {
                console.error('Помилка отримання процедури:', error);
                setError('Не вдалося отримати процедури. Спробуйте ще раз.');
                setLoading(false);
            }
        }

        fetchProceduresData();
    }, []);

    return (
        <Container className="d-flex flex-column">
            {loading ? (
                <Spinner animation="border" role="status">
                    <span className="sr-only">Loading...</span>
                </Spinner>
            ) : error ? (
                <p>{error}</p>
            ) : (
                <>
                    <Table striped bordered hover className="mt-4">
                        <thead>
                            <tr>
                                <th>ID</th>
                                <th>Procedure Name</th>
                                <th>Procedure Description</th>
                                <th>Price</th>
                                <th>Category</th>
                                <th>Дії</th>
                            </tr>
                        </thead>
                        <tbody>
                            {procedures.map(procedure => (
                                <tr key={procedure.procedure_id}>
                                    <td>{procedure.procedure_id}</td>
                                    <td>{procedure.name}</td>
                                    <td>{procedure.description}</td>
                                    <td>{procedure.price}</td>
                                    <td>{procedure.category_id}</td>
                                    <td>
                                        <Button variant="primary" onClick={() => handleEdit(category)}>Редагувати</Button>{' '}
                                        <Button variant="danger" onClick={() => handleDelete(category)}>Видалити</Button>
                                    </td>
                                </tr>
                            ))}
                        </tbody>
                    </Table>

                    <Button className="add-button" onClick={() => setProcedureVisible(true)} >
                        Додати процедуру
                    </Button>
                    <CreateProcedure
                        show={procedureVisible}
                        onHide={() => setProcedureVisible(false)}
                        onUpdateProcedures={handleUpdateProcedures} // Pass the callback function
                    />
                </>
            )}
        </Container>
    );
});

export default AdmProcedure;
